mapper.select:
SELECT TopicID,Title, Summary,PicUrl,TopicType,Recommend,Status,StatusCode,Source,AddTime,ModifyTime,UpdateTime FROM TopicEntity WHERE TopicID IN
#{id}
order by statusCode desc # desc从大到小 asc返回结果list中从小到大 # limit 1

mapper.update:
UPDATE TopicEntity
SET Title = #{title}, Summary = #{summary}, PicUrl = #{picUrl}, TopicType = #{topicType}
, Recommend = #{recommend}, Status = #{status}, StatusCode = #{statusCode},ModifyTime = #{modifyTime}
WHERE TopicID = #{topicId}

mapper.select:
